import React from 'react'
import { Header, Footer, Aside } from './components'
import './styles.css'

const Layout = ({children, showAside}) => {

    return(
        <div className='layout'>
            {showAside 
                ? <Aside />
                : ''
            }

            <div className='contenedor'>
                <Header />
                {children}
                <Footer />
            </div>
        </div>
    )
}

export default Layout
