import React, { useState } from 'react'

const LikeButton = () => {

    const [isLiked, setIsLiked] = useState(false)
    const [text, setText] = useState('Like')

    const handleClick = () => {
        console.log("Mi click de botón funciona")
        setText('Dislike')
        setIsLiked(true)
    }

    return (
        <button className={`btn ${isLiked ? 'btn-primary' : 'btn-secondary' }`} onClick={handleClick}>
            {text}
        </button>
    )
}

export { LikeButton }
