import React from 'react'
import { Header, Footer, Aside } from './components'
import './styles.css'

const Layout = ({children, hide}) => {

    return(
        <div className='layout'>
            { !hide && <Aside /> }

            <div className='contenedor'>
                { !hide && <Header /> }
                {children}
                { !hide && <Footer /> }
            </div>
        </div>
    )
}

Layout.defaultProps = {
    hideHeader: false,
    hideAside: false,
    hideFooter: false
}

export default Layout
