import React from 'react'
import { Header, Footer, Aside } from './components'
import './styles.css'

const Layout = ({children, hideHeader, hideAside, hideFooter, id}) => {

    return(
        <div className='layout'>
            { !hideAside && <Aside /> }

            <div className='contenedor'>
                { !hideHeader && <Header /> }
                { id }
                {children}
                { !hideFooter && <Footer /> }
            </div>
        </div>
    )
}

Layout.defaultProps = {
    id: 9999,
    hideHeader: false,
    hideAside: false,
    hideFooter: false
}

export default Layout
