import React, { useState } from "react";

export const LoginForm = ({ login }) => {

  const [user, setUser] = useState()
  const [pass, setPass] = useState()

  const handleSubmit = (e) => {
    e.preventDefault()
    login(user, pass)
  }

  const handleInputChange = () => {
    setUser('Hola');
    setPass('Mundo!')
    console.log(user, pass)
  }


  return (
      <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Usuario</label>
            <input type="text" name="user" value={user} className="form-control" onChange={handleInputChange} />
          </div>
          <div>
              <label>Contraseaña</label>
              <input type="text" name="pass" value={pass} className="form-control" />
          </div>

          <button type="submit" className="btn btn-primary">Iniciar Sessión</button>
      </form>
  )
};
