import React, { useState } from 'react'

const LikeButton = () => {

    const [isLiked, setIsLiked] = useState(false)

    const handleClick = () => {
        if(isLiked === true) {
            setIsLiked(false)
        } else {
            setIsLiked(true)
        }
    }

    return (
        <button className={`btn ${isLiked ? 'btn-primary' : 'btn-secondary' }`} onClick={handleClick}>
            Like
        </button>
    )
}

export { LikeButton }
