import React from 'react'
import { Header, Footer, Aside } from './components'
import './styles.css'

const Layout = ({children, showHeader}) => {

    return(
        <div className='layout'>
            <Aside />

            <div className='contenedor'>
                {showHeader 
                    ? <Header />
                    : ''
                }
                {children}
                <Footer />
            </div>
        </div>
    )
}

export default Layout
